// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/*
================================================================================
FaucetV2  ——（EIP-712 凭证 + 位图防重放；链上最小可信边界）
--------------------------------------------------------------------------------
【自托管 Relayer 代付 gas】
- 链上只做验签、位图防重放、白名单、每日系统上限、余额检查与转账。
- 是否必须买票、每日次数/软上限、黑名单/KYC/KYT、设备/IP 风控、Relayer 余额阈值等全部放到链下。

【链下待办 / TODO（交给后端 & codex）】
1) 凭证签发服务（Signer）
   - 读链：tokenWhitelist、tokenDailyCap、合约余额、passPriceWei；
   - 风控：黑名单、KYC/KYT、设备/IP 风险分、每日签发上限、软上限等；
   - 生成 EIP-712 Claim（user, token, amount, day, nonce, deadline, pass）并用 SIGNER_ROLE 地址签名；
   - 维护链下 nonce 与对账日志。
2) Relayer 自托管 & 代付
   - HSM/KMS/签名机托管私钥；暴露受控 API；余额阈值切换“需先买票”模式；队列/限流/退避/报警；
   - 监听 Claimed/PassPaid 事件对账。
3) 买票与对账
   - 用户调用 payPass()（仅收款+事件）；后端监听并标记“当日已购票”；余额不足模式下仅对已购票地址签发凭证。

【未来可能演进（需要改合约时再做）】
- 如需“链上硬闸必须买票/必须 Forwarder/只能 Relayer”再加开关或 EIP-2771，当前保持最小边界与低复杂度。

================================================================================
*/

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/utils/Pausable.sol";
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/// @title FaucetV2（EIP-712 验签 + 位图防重放）
/// @notice 链上最小可信边界：验签、防重放、白名单、每日系统上限、余额检查与转账；
///         payPass() 仅收款+事件（不找零），其是否“必须”由链下策略控制；
///         角色：DEFAULT_ADMIN_ROLE 配置参数与角色；FUNDER_ROLE 入金；TREASURER_ROLE 归集；SIGNER_ROLE 验签信任根。
contract FaucetV2 is AccessControl, ReentrancyGuard, Pausable, EIP712 {
    using SafeERC20 for IERC20;

    // ===== 常量/角色 =====
    uint256 private constant DAY = 1 days;
    bytes32 public constant FUNDER_ROLE = keccak256("FUNDER_ROLE"); // 入金
    bytes32 public constant TREASURER_ROLE = keccak256("TREASURER_ROLE"); // 归集
    bytes32 public constant SIGNER_ROLE = keccak256("SIGNER_ROLE"); // 凭证签发者（EOA/HSM 多签等）

    // EIP-712 类型哈希：Claim(address user,address token,uint256 amount,uint64 day,uint256 nonce,uint256 deadline,bool pass)
    bytes32 private constant _CLAIM_TYPEHASH = keccak256(
        "Claim(address user,address token,uint256 amount,uint64 day,uint256 nonce,uint256 deadline,bool pass)"
    );

    // ===== 事件 =====
    event Claimed(address indexed user, address indexed token, uint256 amount, uint64 day, uint256 nonce);
    event PassPaid(address indexed payer, uint256 amount, uint64 day); // 买票“收款”事件（链下监听）
    event FundsSwept(address indexed asset, address indexed to, uint256 amount); // asset=address(0) 表示 ETH
    event ParamUpdated(bytes32 indexed key, address indexed token, uint256 value);

    // ===== 存储（最小可信边界） =====
    address public treasury; // 国库地址
    mapping(address => bool) public tokenWhitelist; // Token 白名单（必须为 true 才能发放）
    mapping(address => uint256) public tokenDailyCap; // Token 系统日上限（=0 表示无限）

    struct DailyIssued {
        uint64 day;
        uint192 amount;
    } // 打包紧凑：节省 SSTORE（64+192=1 槽）

    mapping(address => DailyIssued) private _issuedToday; // token => 当日已发

    // 防重放：位图 used[user][word]，word = nonce >> 8（每个 word 覆盖 256 连续 nonce）
    mapping(address => mapping(uint256 => uint256)) private _usedNonceBitmap;

    // （可选）链上买票价格（wei）；=0 表示关闭链上买票入口（免费期）
    uint256 public passPriceWei;

    // ===== 自定义错误（省 gas） =====
    error TOKEN_NOT_ALLOWED(address token);
    error VAULT_LOW(address token, uint256 need, uint256 bal);
    error OVER_DAILY_CAP(address token, uint256 next, uint256 cap, uint64 day);
    error INVALID_SIGNER(address recovered);
    error EXPIRED(uint256 nowTs, uint256 deadline);
    error DAY_MISMATCH(uint64 got, uint64 want);
    error NONCE_USED(address user, uint256 nonce);
    error TREASURY_UNSET();
    error SWEEP_ETH_INVALID();
    error SWEEP_FAIL();
    error PRICE_ZERO();
    error UNDERPAY();
    error ZERO_ADDR();

    // ===== 构造 =====
    constructor(address admin, address treasury_, address initialSigner, string memory name_, string memory version_)
        EIP712(name_, version_)
    {
        if (admin == address(0) || treasury_ == address(0)) revert ZERO_ADDR();
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(FUNDER_ROLE, admin);
        _grantRole(TREASURER_ROLE, admin);
        if (initialSigner != address(0)) _grantRole(SIGNER_ROLE, initialSigner);
        treasury = treasury_;
        emit ParamUpdated("TREASURY", treasury_, 0);
    }

    // ===== 视图 =====
    function currentDay() public view returns (uint64) {
        return uint64(block.timestamp / DAY);
    }

    /// @notice 查询合约当前持有的某 Token 余额
    function balanceOfToken(address token) external view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    function _isNonceUsed(address user, uint256 nonce) internal view returns (bool) {
        uint256 word = nonce >> 8;
        uint256 bit = 1 << (nonce & 255);
        return (_usedNonceBitmap[user][word] & bit) != 0;
    }

    // ===== 管理：白名单/上限/价格/国库 =====
    function setTokenWhitelist(address token, bool allowed) external onlyRole(DEFAULT_ADMIN_ROLE) {
        tokenWhitelist[token] = allowed;
        emit ParamUpdated("TOKEN_WHITELIST", token, allowed ? 1 : 0);
    }

    function setTokenDailyCap(address token, uint256 cap) external onlyRole(DEFAULT_ADMIN_ROLE) {
        tokenDailyCap[token] = cap;
        emit ParamUpdated("TOKEN_DAILY_CAP", token, cap);
    }

    function setPassPriceWei(uint256 price) external onlyRole(DEFAULT_ADMIN_ROLE) {
        passPriceWei = price; // 0=免费期；>0=收费期
        emit ParamUpdated("PASS_PRICE", address(0), price);
    }

    function setTreasury(address t) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (t == address(0)) revert ZERO_ADDR();
        treasury = t;
        emit ParamUpdated("TREASURY", t, 0);
    }

    // ===== 入金/归集 =====
    function fund(address token, uint256 amount) external onlyRole(FUNDER_ROLE) {
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
    }

    function sweepETH(uint256 amount) external nonReentrant onlyRole(TREASURER_ROLE) {
        if (treasury == address(0)) revert TREASURY_UNSET();
        if (amount == 0 || amount > address(this).balance) revert SWEEP_ETH_INVALID();
        (bool ok,) = payable(treasury).call{value: amount}("");
        if (!ok) revert SWEEP_FAIL();
        emit FundsSwept(address(0), treasury, amount);
    }

    function sweepToken(address token, uint256 amount) external nonReentrant onlyRole(TREASURER_ROLE) {
        IERC20(token).safeTransfer(treasury, amount);
        emit FundsSwept(token, treasury, amount);
    }

    // ===== 可选：链上买票，仅收款 + 事件（不找零；多付留存）=====
    function payPass() external payable nonReentrant whenNotPaused {
        uint256 price = passPriceWei;
        if (price == 0) revert PRICE_ZERO();
        if (msg.value < price) revert UNDERPAY();

        uint64 d = currentDay();
        // 不找零：多付部分留在合约作为收入（由 sweepETH 归集）
        emit PassPaid(msg.sender, msg.value, d);
    }

    // ======= 参数结构体：用于降低 claimWithSig 的栈深 =======
    struct ClaimReq {
        address user;
        address token;
        uint256 amount;
        uint64 day;
        uint256 nonce;
        uint256 deadline;
        bool pass;
    }

    // ===== 内部：单独的事件封装，减少外层函数的栈压力 =====
    function _emitClaim(address u, address t, uint256 a, uint64 d, uint256 n) private {
        emit Claimed(u, t, a, d, n);
    }

    // ===== 核心：凭证领取（Relayer 代付场景：由 Relayer 发交易） =====
    function claimWithSig(ClaimReq calldata p, bytes calldata sig) external nonReentrant whenNotPaused {
        // --- 快速校验（直接用 p.字段减少临时变量） ---
        if (!tokenWhitelist[p.token]) revert TOKEN_NOT_ALLOWED(p.token);

        uint64 dNow = currentDay();
        if (p.day != dNow) revert DAY_MISMATCH(dNow, p.day);
        if (block.timestamp > p.deadline) revert EXPIRED(block.timestamp, p.deadline);

        // --- 防重放：位图（放入局部作用域，缩短 w/b/used 生命周期） ---
        {
            uint256 w = p.nonce >> 8;
            uint256 b = 1 << (p.nonce & 255);
            uint256 used = _usedNonceBitmap[p.user][w];
            if ((used & b) != 0) revert NONCE_USED(p.user, p.nonce);
            _usedNonceBitmap[p.user][w] = used | b;
        }

        // --- EIP-712 验签（放入作用域，digest/signer 生命周期最短） ---
        {
            bytes32 digest = _hashTypedDataV4(
                keccak256(abi.encode(_CLAIM_TYPEHASH, p.user, p.token, p.amount, p.day, p.nonce, p.deadline, p.pass))
            );
            address signer = ECDSA.recover(digest, sig);
            if (!hasRole(SIGNER_ROLE, signer)) revert INVALID_SIGNER(signer);
        }

        // --- 系统日上限护栏（按 token；合约维度） ---
        DailyIssued storage di = _issuedToday[p.token];
        if (di.day != dNow) {
            di.day = dNow;
            di.amount = 0;
        }
        uint256 next = uint256(di.amount) + p.amount;

        uint256 cap = tokenDailyCap[p.token];
        if (cap != 0 && next > cap) revert OVER_DAILY_CAP(p.token, next, cap, dNow);

        // --- 余额护栏（内联，避免持有额外的局部变量） ---
        if (IERC20(p.token).balanceOf(address(this)) < p.amount) {
            revert VAULT_LOW(p.token, p.amount, IERC20(p.token).balanceOf(address(this)));
        }

        // --- Effects & Interactions ---
        di.amount = uint192(next);
        IERC20(p.token).safeTransfer(p.user, p.amount);

        // --- 事件放到内部函数单独调用帧，避免与外层变量堆叠 ---
        _emitClaim(p.user, p.token, p.amount, dNow, p.nonce);
    }

    // ===== 熔断 =====
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // ===== 接收 ETH（买票收入、意外注入）=====
    receive() external payable {}
}

src/guard/GuardedRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IOracleRouter} from "src/interfaces/IOracleRouter.sol";
import {IUniswapV2Factory} from "src/interfaces/IUniswapV2Factory.sol";
import {IUniswapV2Pair} from "src/interfaces/IUniswapV2Pair.sol";

/// @title GuardedRouter（软约束版，仅视图预检，不代理交易/不托管资产）
/// @notice 提供对单跳 V2 交易的“价格偏离 / 预言机过期”检查，前端用 eth_call 调用；
///         通过后由前端/后端直接向 Router/Pair 下单。本合约不做转账、授权、路由调用。
/// @dev Gas 侧重点：
///      - factory immutable（部署期少一次 SSTORE）；
///      - 全局参数 Defaults 单槽打包（2B+2B+4B）；
///      - Pair 覆盖 PairCfg 单槽打包（2B+4B+1B）；
///      - 方向归一：_oriented() 统一对齐 base→quote 储备，后续无分支；
///      - 构造不 emit；仅 set* 事件（可按需删除进一步省字节码）。
contract GuardedRouter is Ownable2Step {
    // ===== 自定义错误（替代 require/字符串，节省部署与运行期 gas） =====
    error ZeroAddress();
    error PathLength();  // path.length != 2
    error PathArgs();    // 零地址或重复地址

    // ===== 常量（运行期不占存储） =====
    uint256 private constant FEE_NUM = 997;    // Uniswap V2 0.3% 手续费的“有效系数”
    uint256 private constant FEE_DEN = 1000;

    // ===== 单槽全局默认参数 =====
    /// @param hardBps      正常价源下的价格偏离硬阈值（基点，10000=100%）
    /// @param hardBpsFixed 任一侧为 Fixed 价源时的放宽阈值（基点）
    /// @param staleSec     正常价源下的过期秒数
    struct Defaults { uint16 hardBps; uint16 hardBpsFixed; uint32 staleSec; }
    Defaults public defaults; // 1 槽

    struct PairCfg { uint16 hardBps; uint32 staleSec; uint8 enabled; }

    /// @notice 工厂地址（不可变，读便宜、部署不 SSTORE）
    IUniswapV2Factory public immutable factory;

    /// @notice 预言机（可热切换）
    IOracleRouter public oracle; // 1 槽

    /// @dev 无序对 → 覆盖配置；键为排序后 (min, max) 的哈希
    mapping(bytes32 => PairCfg) public pairCfg;

    // ===== 事件（可按需删除以进一步省字节码） =====
    event DefaultsUpdated(uint16 hardBps, uint16 hardBpsFixed, uint32 staleSec);
    event PairCfgUpdated(address indexed tokenA, address indexed tokenB, PairCfg cfg);
    event OracleUpdated(address indexed newOracle);

    // ===== 构造（不 emit，减少部署成本） =====
    constructor(
        address _factory,
        address _oracle,
        uint16 _hardBps,
        uint16 _hardBpsFixed,
        uint32 _staleSec,
        address initialOwner
    ) Ownable(initialOwner) {
        if (_factory == address(0) || _oracle == address(0)) revert ZeroAddress();
        factory  = IUniswapV2Factory(_factory);     // immutable → 不写存储
        oracle   = IOracleRouter(_oracle);          // 1 槽
        defaults = Defaults(_hardBps, _hardBpsFixed, _staleSec); // 1 槽
    }

    // ===== 仅 owner 的管理入口 =====
    function setOracleRouter(address _oracle) external onlyOwner {
        if (_oracle == address(0)) revert ZeroAddress();
        oracle = IOracleRouter(_oracle);            // 非0→非0 ≈ 5k gas
        emit OracleUpdated(_oracle);
    }

    function setDefaults(uint16 _hardBps, uint16 _hardBpsFixed, uint32 _staleSec) external onlyOwner {
        defaults = Defaults(_hardBps, _hardBpsFixed, _staleSec); // 非0→非0 ≈ 5k gas
        emit DefaultsUpdated(_hardBps, _hardBpsFixed, _staleSec);
    }

    /// @notice 设置/关闭某对的覆盖（cfg.enabled=0 视为不用覆盖）
    function setPairCfg(address tokenA, address tokenB, PairCfg calldata cfg) external onlyOwner {
        (bytes32 key, address x, address y) = _pairKeySorted(tokenA, tokenB);
        pairCfg[key] = cfg; // 首次写入 ≈ 20k，更新 ≈ 5k
        emit PairCfgUpdated(x, y, cfg);
    }

    // ===== 只读：价格快照（前端展示用） =====
    /// @return dexMidE18   当前池子中价（quote/base，1e18）
    /// @return oraclePxE18 预言机价（1e18）
    /// @return updatedAt   预言机更新时间
    /// @return stale       是否过期（Fixed 不判过期）
    /// @return limitBps    本次检查使用的偏离阈值（Fixed 会放宽）
    /// @return srcFixed    是否涉及 Fixed 价源（用于 UI 提示）
    function checkPriceNow(address base, address quote)
        external
        view
        returns (uint256 dexMidE18, uint256 oraclePxE18, uint256 updatedAt, bool stale, uint16 limitBps, bool srcFixed)
    {
        Oriented memory o = _oriented(base, quote);
        if (o.pair == address(0) || o.reserveBase == 0) return (0, 0, 0, true, 0, false);
        dexMidE18 = (uint256(o.reserveQuote) * 1e18) / uint256(o.reserveBase);

        (oraclePxE18, updatedAt, limitBps, stale, srcFixed) = _oracleAndPolicy(base, quote);
    }

    // ===== 只读：单跳 Exact-In 预检（软约束） =====
    function checkSwapExactIn(address[] calldata path, uint256 amountIn)
        external
        view
        returns (bool ok, uint256 devBps, uint256 limitBps, bool stale, uint256 dexAfterE18, uint256 oracleE18)
    {
        _validatePath2(path);

        (oracleE18, limitBps, stale) = _oracleAndLimit(path[0], path[1]);
        if (stale) return (false, 0, limitBps, true, 0, oracleE18);

        (address pair, uint112 rBase, uint112 rQuote) = _orientedReserves(path[0], path[1]);
        if (pair == address(0) || rBase == 0) return (false, 0, limitBps, false, 0, oracleE18);

        // out = (in*997*resOut) / (resIn*1000 + in*997)
        uint256 inWithFee = amountIn * FEE_NUM;
        uint256 out = (inWithFee * rQuote) / (uint256(rBase) * FEE_DEN + inWithFee);

        // 仅为 dexAfter 计算新储备（不引入多余局部变量）
        dexAfterE18 = ((uint256(rQuote) - out) * 1e18) / (uint256(rBase) + amountIn);

        // 直接用实际成交均价 vs 预言机价计算偏离，避免 execPxE18 变量常驻
        uint256 pxExec = (amountIn == 0) ? 0 : (out * 1e18) / amountIn;
        devBps = _devBps(pxExec, oracleE18);
        ok = (devBps <= limitBps);
    }

    // ===== 只读：单跳 Exact-Out 预检（软约束） =====
    function checkSwapExactOut(address[] calldata path, uint256 amountOut)
        external
        view
        returns (bool ok, uint256 devBps, uint256 limitBps, bool stale, uint256 dexAfterE18, uint256 oracleE18, uint256 amountInNeeded)
    {
        _validatePath2(path);

        (oracleE18, limitBps, stale) = _oracleAndLimit(path[0], path[1]);
        if (stale) return (false, 0, limitBps, true, 0, oracleE18, 0);

        (address pair, uint112 rBase, uint112 rQuote) = _orientedReserves(path[0], path[1]);
        if (pair == address(0) || rBase == 0 || amountOut >= rQuote)
            return (false, 0, limitBps, false, 0, oracleE18, 0);

        // in = ceil(resIn*out*1000 / ((resOut - out)*997))
        uint256 num = uint256(rBase) * amountOut * FEE_DEN;
        uint256 den = (uint256(rQuote) - amountOut) * FEE_NUM;
        amountInNeeded = (num + den - 1) / den;

        // dexAfter
        dexAfterE18 = ((uint256(rQuote) - amountOut) * 1e18) / (uint256(rBase) + amountInNeeded);

        // 与 oracle 的偏离（使用实际成交均价，不保留中间变量）
        uint256 pxExec = (amountInNeeded == 0) ? 0 : (amountOut * 1e18) / amountInNeeded;
        devBps = _devBps(pxExec, oracleE18);
        ok = (devBps <= limitBps);
    }

    // ================= 内部：策略 / 方向归一 / 工具 =================

    /// @dev 读取公允价并计算“本次应使用的阈值/是否过期”
    ///      - src==Fixed：不过期；阈值取 max(pairHardBps, hardBpsFixed)
    ///      - UsdSplit  ：按 staleSec 判过期；阈值取 pairHardBps
    function _oracleAndPolicy(address base, address quote)
        internal
        view
        returns (uint256 oracleE18, uint256 updatedAt, uint16 limitBps, bool stale, bool srcFixed)
    {
        IOracleRouter.PriceSrc src;
        (oracleE18, updatedAt, src) = oracle.latestAnswer(base, quote);

        (uint16 hardBps, uint32 staleSec) = _pairPolicy(base, quote);

        if (src == IOracleRouter.PriceSrc.Fixed) {
            srcFixed = true;
            stale    = false;
            limitBps = (hardBps >= defaults.hardBpsFixed) ? hardBps : defaults.hardBpsFixed;
        } else {
            srcFixed = false;
            stale    = (block.timestamp - updatedAt) > staleSec;
            limitBps = hardBps;
        }
    }


        /// @dev 只拿交易需要的三项：公允价 / 偏离阈值 / 是否过期（减少一次多返回解构）
    function _oracleAndLimit(address base, address quote)
        internal
        view
        returns (uint256 oracleE18, uint16 limitBps, bool stale)
    {
        IOracleRouter.PriceSrc src;
        uint256 updatedAt;
        (oracleE18, updatedAt, src) = oracle.latestAnswer(base, quote);

        (uint16 hardBps, uint32 staleSec) = _pairPolicy(base, quote);

        if (src == IOracleRouter.PriceSrc.Fixed) {
            stale    = false;
            limitBps = (hardBps >= defaults.hardBpsFixed) ? hardBps : defaults.hardBpsFixed;
        } else {
            stale    = (block.timestamp - updatedAt) > staleSec;
            limitBps = hardBps;
        }
    }

    /// @dev 返回面向 base→quote 的储备，不再构造 struct，减少栈占用
    function _orientedReserves(address base, address quote)
        internal
        view
        returns (address pair, uint112 rBase, uint112 rQuote)
    {
        pair = factory.getPair(base, quote);
        if (pair == address(0)) return (address(0), 0, 0);
        (uint112 r0, uint112 r1,) = IUniswapV2Pair(pair).getReserves();
        address t0 = IUniswapV2Pair(pair).token0();
        if (t0 == base) {
            rBase = r0; rQuote = r1;
        } else {
            rBase = r1; rQuote = r0;
        }
    }


    /// @dev 方向归一：把 pair 的 (r0,r1) 变成“面向 base→quote”的 (reserveBase,reserveQuote)
    struct Oriented {
        address pair;
        bool    baseIsT0;
        uint112 reserveBase;
        uint112 reserveQuote;
    }

    function _oriented(address base, address quote) private view returns (Oriented memory o) {
        address p = factory.getPair(base, quote);
        if (p == address(0)) return o; // o.pair=0 代表无池
        (uint112 r0, uint112 r1,) = IUniswapV2Pair(p).getReserves();
        address t0 = IUniswapV2Pair(p).token0();
        o.pair = p;
        if (t0 == base) {
            o.baseIsT0     = true;
            o.reserveBase  = r0;
            o.reserveQuote = r1;
        } else {
            o.baseIsT0     = false;
            o.reserveBase  = r1;
            o.reserveQuote = r0;
        }
    }

    /// @dev 统一计算“本对使用的偏离阈值与过期阈值”（优先取覆盖；为 0 则回落到全局）
    function _pairPolicy(address a, address b) private view returns (uint16 hardBps, uint32 staleSec) {
        (bytes32 key,,) = _pairKeySorted(a, b);
        PairCfg memory cfg = pairCfg[key];
        if (cfg.enabled != 0) {
            hardBps  = (cfg.hardBps  == 0) ? defaults.hardBps  : cfg.hardBps;
            staleSec = (cfg.staleSec == 0) ? defaults.staleSec : cfg.staleSec;
        } else {
            hardBps  = defaults.hardBps;
            staleSec = defaults.staleSec;
        }
    }

    /// @dev 生成“无序对”的键：对 (a,b) 排序得到 (x=min, y=max)，并返回 key 与排序后的 x,y。
    function _pairKeySorted(address a, address b) private pure returns (bytes32 key, address x, address y) {
        (x, y) = a < b ? (a, b) : (b, a);
        key = keccak256(abi.encodePacked(x, y));
    }

    /// @dev 最小校验（软约束）：只保证 path 基本合理
    function _validatePath2(address[] calldata path) private pure {
        if (path.length != 2) revert PathLength();
        if (path[0] == address(0) || path[1] == address(0) || path[0] == path[1]) revert PathArgs();
    }

    /// @dev 偏离（基点）。若任一价格为 0，直接返回最大值用于标记“不可用/不通过”。
    function _devBps(uint256 dexPxE18, uint256 oraclePxE18) private pure returns (uint256) {
        if (dexPxE18 == 0 || oraclePxE18 == 0) return type(uint256).max;
        if (dexPxE18 > oraclePxE18) return ((dexPxE18 - oraclePxE18) * 10_000) / oraclePxE18;
        return ((oraclePxE18 - dexPxE18) * 10_000) / oraclePxE18;
    }
}

src/interfaces/IOracleRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/// @title IOracleRouter
/// @notice 统一的“预言机路由”接口：
///         - 任意 base/quote 返回公允价（1e18精度）+ 更新时间 + 价源类型（src）
///         - 也可直接查询 token 的 USD 价格（1e18精度）
/// @dev    Guard 侧只依赖本接口；具体实现由 ChainlinkOracle 等提供
interface IOracleRouter {
    /// @dev 价源类型：
    ///      - UsdSplit: 由 base/USD 与 quote/USD 拼出 base/quote
    ///      - Fixed   : 固定锚价（用于测试网/无Feed场景），不参与 staleness 判定
    enum PriceSrc { UsdSplit, Fixed }

    /// @notice 查询 base/quote 的公允价（1e18）与更新时间与价源类型
    /// @param base   计价基准（分子）
    /// @param quote  计价标的（分母）
    /// @return priceE18  价格(1e18)，含义：1 base = priceE18 / 1e18 个 quote
    /// @return updatedAt 该价格的 on-chain 更新时间（两侧USD价的最小值；双Fixed时给当前块时间）
    /// @return src       价源类型（UsdSplit / Fixed）
    function latestAnswer(address base, address quote)
        external
        view
        returns (uint256 priceE18, uint256 updatedAt, PriceSrc src);

    /// @notice 查询 token 的 USD 价格（1e18）与更新时间
    /// @dev    为保持接口简洁，这里不返回 src；Guard 一般使用 latestAnswer()
    /// @param  token  资产地址
    /// @return priceE18  价格(1e18)，含义：1 token = priceE18 / 1e18 USD
    /// @return updatedAt 链上更新时间（Fixed 场景返回当前块时间）
    function getUSDPrice(address token)
        external
        view
        returns (uint256 priceE18, uint256 updatedAt);
}

