  error MustBeProposedOwner();
  error CannotTransferToSelf();
  error OnlyCallableByOwner();

  event OwnershipTransferRequested(address indexed from, address indexed to);
  event OwnershipTransferred(address indexed from, address indexed to);

  constructor(address newOwner, address pendingOwner) {
    if (newOwner == address(0)) revert OwnerCannotBeZero();

    s_owner = newOwner;
    if (pendingOwner != address(0)) {
      _transferOwnership(pendingOwner);
    }
  }

  /// @notice Get the current owner.
  function owner() public view override returns (address) {
    return s_owner;
  }

  /// @notice Begin transferring ownership to a new address (two-step).
  function transferOwnership(address to) public override onlyOwner {
    _transferOwnership(to);
  }

  /// @notice validate, set pending owner, and emit events.
  function _transferOwnership(address to) private {
    if (to == msg.sender) revert CannotTransferToSelf();

    s_pendingOwner = to;
    emit OwnershipTransferRequested(s_owner, to);
  }

  /// @notice Complete the transfer by the pending owner.
  function acceptOwnership() external override {
    if (msg.sender != s_pendingOwner) revert MustBeProposedOwner();

    address oldOwner = s_owner;
    s_owner = msg.sender;
    s_pendingOwner = address(0);

    emit OwnershipTransferred(oldOwner, msg.sender);
  }

